    def convert_pdf(self):
        print('oi')
        print(self.pdf)
        print(self.format)
        self.file = self.pdf.split('.')[0]
        self.new_file = self.file + '.' + self.format
        self.csv = ''
        if self.format == 'XLS' or self.format == 'XLSX' or self.format == 'csv':
            print('estou aqui')
            tb.convert_into(self.pdf, self.csv, output_format='csv', pages='all')
            self.df = pd.read_csv(self.csv)
            if self.format == 'XLS' or self.format == 'XLSX':
                print('XLS')
                self.df.to_excel(self.new_file, index=False)
            else:
                self.df.to_csv(self.new_file, index=False)
        elif self.format == 'DOC' or self.format == 'DOCX':
            self.df = pdf.parse(self.pdf)
            self.df.to_csv(self.new_file, index=False)
